mapper.update:
UPDATE board_comment
SET is_active = 0
WHERE board_comment_id = #{boardCommentId}

UPDATE board_comment
SET like_count = like_count + 1
WHERE board_comment_id = #{boardCommentId}

mapper.select:
SELECT like_count
FROM board_comment
WHERE board_comment_id = #{boardCommentId}

mapper.delete:
DELETE FROM board_comment_like
WHERE board_comment_id = #{boardCommentId}
AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM board_comment_like
WHERE board_comment_id = #{boardCommentId}
AND user_id = #{userId}
