mapper.update:
UPDATE board
SET is_active = 0
WHERE board_id = #{boardId}

UPDATE board
SET like_count = like_count + 1
WHERE board_id = #{boardId}

mapper.select:
SELECT like_count
FROM board
WHERE board_id = #{boardId}

mapper.delete:
DELETE FROM board_like
WHERE board_id = #{boardId}
AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM board_like
WHERE board_id = #{boardId}
AND user_id = #{userId}
